exports.parse_input = function(user_input){
  user_input = user_input.trim();
  user_input = user_input.replace(/\n?\r\n/g, '<br />' );
  user_input = user_input.replace('<script', '<<i></i>script' );
  return user_input
};

exports.get_picture = function(user){
	if (!user) { return ""; }
	return "http://graph.facebook.com/" + user.username + "/picture?type=large"
}

exports.parse_date = function(date) {
	if (date == "") {
		return null;
	} else {
		var slash = date.indexOf("/");
		var sec_slash = date.lastIndexOf("/");
		var month = date.substring(0,slash);
		var day = date.substring(slash+1,sec_slash);
		var year = date.substring(sec_slash+1, date.length);
		var date = new Date(year, month-1, day);
		return date;
	}
}
